import React from 'react';
import { Col, Row, Button, Typography, Space, Layout, Card } from 'antd';
import Navbar from '../components/Navbar';
import FooterComponent from '../components/footer';

const { Title, Text } = Typography;
const { Content } = Layout;

function Boleteria() {
  return (
    <Layout className="background">
      <Row>
        <Col span={24}>
          <Navbar />
        </Col>
      </Row>

      <Content style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', padding: '24px' }}>
        <Card style={{ width: '100%', maxWidth: '600px', textAlign: 'center', backgroundColor: '#dac178' }} bodyStyle={{ padding: '20px' }} bordered={false}>
          
          {/* Contenedor de la "Pantalla" */}
          <div style={{ marginBottom: '20px', textAlign: 'center' }}>
            <div style={{ width: '80%', height: '30px', backgroundColor: '#6c757d', borderRadius: '8px', margin: '0 auto', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
              <Title level={3} style={{ color: '#242424', fontSize: '16px', margin: 0 }}>Pantalla</Title>
            </div>
          </div>

          {/* Indicadores de estado */}
          <Space direction="vertical" style={{ marginBottom: '20px' }}>
            <Space>
              <div style={{ width: '10px', height: '10px', backgroundColor: '#28a745', borderRadius: '50%' }}></div>
              <Text strong>Seleccionado</Text>
            </Space>
            <Space>
              <div style={{ width: '10px', height: '10px', backgroundColor: '#dc3545', borderRadius: '50%' }}></div>
              <Text strong>Ocupado</Text>
            </Space>
          </Space>

          {/* Botones de asientos */}
          <div>
            {['A', 'B', 'C', 'D'].map(row => (
              <Row key={row} justify="center" gutter={[16, 16]} style={{ marginBottom: '8px' }}> {/* Separación vertical */}
                {[1, 2, 3, 4, 5].map(seat => (
                  <Col key={`${row}${seat}`}>
                    <Button 
                      type="default" 
                      style={{ 
                        borderRadius: '4px', 
                        width: '40px', 
                        height: '40px', 
                        marginBottom: '8px', 
                        backgroundColor: '#6c757d', // Color gris para los botones
                        borderColor: '#6c757d' // Color de borde gris para los botones
                      }} 
                    >
                      {`${row}${seat}`}
                    </Button>
                  </Col>
                ))}
              </Row>
            ))}
          </div>
        </Card>

        {/* Botones de navegación */}
        <Row justify="space-between" style={{ width: '100%', maxWidth: '600px', marginTop: '20px' }}>
          <Col>
            <Button href="#" type="default">Atrás</Button>
          </Col>
          <Col>
            <Button href="recibo.html" type="default">Siguiente</Button>
          </Col>
        </Row>
      </Content>

      <FooterComponent />
    </Layout>
  );
}

export default Boleteria;
